drop database if exists JustRead;
create database JustRead;

create table Users(
	user_username varchar(50) primary key,
	user_password varchar(50),
	user_fullname varchar(100),
	user_avatar varchar(100),
	user_date_of_birth date,
	user_created_date date,
	user_is_admin int default 0
);

create table Category(
	category_id int primary key identity(1,1),
	category_name varchar(50)
);


create table Book(
	book_id varchar(50) primary key,
	book_category_id int foreign key references Category(category_id),
	book_type varchar(20), 
	book_title varchar(500),
	book_author varchar(500),
	book_public_date varchar(10),
	book_rating float,
	book_rated_time int,
	book_download int,
	book_page int,
	book_description varchar(4000),
	book_image varchar(500),
	book_file varchar(100),
	book_upload_date date,
	book_is_deleted int default 0
);

create table ReadingHistory(
	read_id int primary key identity(1,1),
	read_username varchar(50) foreign key references Users(user_username),
	read_book_id varchar(50),
	read_started_time datetime,
	read_ended_time datetime,
	read_is_deleted int
);

create table Comment(
	comment_id int primary key identity(1,1),
	comment_book_id varchar(50) foreign key references Book(book_id),
	comment_username varchar(50),
	comment_content varchar(500),
	comment_is_deleted int
);



